const input = $input.first().json.body || $input.first().json;

try {
  const action = (input.action || UNKNOWN).toUpperCase();
  const agent_name = input.agent_name || Unknown Agent;
  const details = input.details || ;
  const workflow_id = input.workflow_id || ;
  const user = input.user || system;
  const timestamp = new Date().toISOString();

  let logLine = `[${timestamp}] [${action}] Agent: ${agent_name}`;

  if (workflow_id) {
    logLine += ` | ID: ${workflow_id}`;
  }

  if (details) {
    logLine += ` | Details: ${details}`;
  }

  logLine += ` | User: ${user}`;

  return [{
    json: {
      status: success,
      log_line: logLine,
      timestamp: timestamp,
      action: action,
      agent_name: agent_name
    }
  }];
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Agent Masterlog,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Processing log entry,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Agent Masterlog,
      timestamp: new Date().toISOString()
    }
  }];
}

