const input = $input.first().json.body || $input.first().json;

try {
  const logEntry = {
    ...input,
    timestamp: new Date().toISOString(),
    logged_at: Date.now()
  };

  return [{ json: logEntry }];
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Agent Masterlog,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Preparing log entry with timestamp,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Agent Masterlog,
      timestamp: new Date().toISOString()
    }
  }];
}

