const input = $input.first().json.body || $input.first().json;

try {
  const path = input.path;
  const content = input.content;
  const message = input.message || Update file;

  if (!path || !content) {
    throw new Error(Missing required fields: path and content);
  }

  const fullPath = `/home/node/glumpe-ai-agents/${path}`;
  const escapedContent = content.replace(//g,

)

  return [{
    json: {
      fullPath: fullPath,
      escapedContent: escapedContent,
      message: message,
      path: path
    }
  }];
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: GitHub Push File,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Preparing file push to GitHub,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: GitHub Push File,
      timestamp: new Date().toISOString()
    }
  }];
}

