const input = $input.first().json.body || $input.first().json;

try {
  const query = input.query || input.message || input.text || ;

  if (!query) {
    throw new Error(No query provided);
  }

  return [{
    json: {
      chatInput: query,
      originalQuery: query
    }
  }];
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Healthcare Bot V3,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Extracting user query,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Healthcare Bot V3,
      timestamp: new Date().toISOString()
    }
  }];
}

const input = $input.first().json;

try {
  const response = input.output || input.text || ;
  const query = input.chatInput || ;

  // Extract category from [CATEGORY: X] pattern
  const categoryMatch = response.match(/\[CATEGORY:\s*([^\]]+)\]/i);
  const category = categoryMatch ? categoryMatch[1].trim() : Other;

  // Remove category tag from response
  const cleanedResponse = response.replace(/\[CATEGORY:\s*[^\]]+\]/gi, ).trim();

  return [{
    json: {
      timestamp: new Date().toISOString(),
      query: query,
      response: cleanedResponse,
      category: category,
      fullResponse: response
    }
  }];
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Healthcare Bot V3,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Categorizing response,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Healthcare Bot V3,
      timestamp: new Date().toISOString()
    }
  }];
}

