const input = $input.first().json.body || $input.first().json;

try {
  // This agent needs SSH capabilities to read remote files
  throw new Error(NEED_TOOL: SSH client to connect to server and execute cat /agents/memory/agent-masterlog.md command);
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Masterlog Reader Agent,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Reading masterlog file from remote server via SSH,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Masterlog Reader Agent,
      timestamp: new Date().toISOString()
    }
  }];
}
