const input = $input.first().json.body || $input.first().json;

try {
  if (!input.path || !input.content || !input.message) {
    throw new Error(Missing required fields: path, content, message);
  }

  const escapedContent = input.content.replace(//g,

);
  const fullPath = `/opt/glumpe-ai-agents/${input.path}`;
  const writeCommand = `cat << EOF > ${fullPath}\n${input.content}\nEOF`;

  return [{
    json: {
      writeCommand: writeCommand,
      commitMessage: input.message,
      path: input.path,
      fullPath: fullPath
    }
  }];
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Push to GitHub,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Preparing file write and git commit,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Push to GitHub,
      timestamp: new Date().toISOString()
    }
  }];
}

const input = $input.first().json;

try {
  return [{
    json: {
      status: success,
      message: File written and pushed to GitHub,
      path: $(Prepare Commands).item.json.path,
      commit_message: $(Prepare Commands).item.json.commitMessage,
      git_output: input.stdout || input,
      timestamp: new Date().toISOString()
    }
  }];
} catch (error) {
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Push to GitHub,
      timestamp: new Date().toISOString()
    }
  }];
}
