const input = $input.first().json.body || $input.first().json;

try {
  const ticker = input.ticker;

  if (!ticker) {
    return [{
      json: {
        status: error,
        error: Ticker symbol is required,
        agent_name: Stock Price Agent,
        timestamp: new Date().toISOString()
      }
    }];
  }

  return [{
    json: {
      ticker: ticker.toUpperCase(),
      original_input: input
    }
  }];
} catch (error) {
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Stock Price Agent,
      timestamp: new Date().toISOString()
    }
  }];
}

const input = $input.first().json;

try {
  if (input.status === success) {
    const message = `ðŸ“ˆ Stock Price for ${input.ticker}:\n\nPrice: ${input.price} ${input.currency}`;

    return [{
      json: {
        status: success,
        ticker: input.ticker,
        price: input.price,
        currency: input.currency,
        message: message,
        agent_name: Stock Price Agent,
        timestamp: new Date().toISOString()
      }
    }];
  } else {
    return [{
      json: {
        status: error,
        error: input.error || Failed to fetch stock price,
        agent_name: Stock Price Agent,
        timestamp: new Date().toISOString()
      }
    }];
  }
} catch (error) {
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Stock Price Agent,
      timestamp: new Date().toISOString()
    }
  }];
}
