const input = $input.first().json.body || $input.first().json;

try {
  const ticker = input.ticker || input.symbol;

  if (!ticker) {
    return [{
      json: {
        status: error,
        error: No ticker symbol provided,
        agent_name: Stock Price Agent,
        timestamp: new Date().toISOString()
      }
    }];
  }

  // Signal that this agent needs a stock price API tool
  throw new Error(NEED_TOOL: Stock price API to fetch real-time or delayed stock prices for ticker symbols);

} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Stock Price Agent,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Fetching stock price for ticker:  + (input.ticker || input.symbol),
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Stock Price Agent,
      timestamp: new Date().toISOString()
    }
  }];
}
