const input = $input.first().json.body || $input.first().json;

try {
  const ticker = input.ticker || input.symbol;

  if (!ticker) {
    throw new Error(Missing required parameter: ticker);
  }

  return [{
    json: {
      ticker: ticker.toUpperCase(),
      url: `https://query1.finance.yahoo.com/v8/finance/chart/${ticker.toUpperCase()}`
    }
  }];
} catch (error) {
  return [{
    json: {
      status: error,
      error: error.message,
      tool_name: Stock Price Tool,
      timestamp: new Date().toISOString()
    }
  }];
}

const input = $input.first().json;

try {
  const ticker = input.chart?.result?.[0]?.meta?.symbol;
  const price = input.chart?.result?.[0]?.meta?.regularMarketPrice;
  const currency = input.chart?.result?.[0]?.meta?.currency;

  if (!price) {
    throw new Error(Unable to fetch stock price - ticker may be invalid);
  }

  return [{
    json: {
      status: success,
      ticker: ticker,
      price: price,
      currency: currency,
      timestamp: new Date().toISOString()
    }
  }];
} catch (error) {
  return [{
    json: {
      status: error,
      error: error.message,
      tool_name: Stock Price Tool,
      timestamp: new Date().toISOString()
    }
  }];
}
