const webhookInput = $input.first().json.body || $input.first().json;
const screenerData = $input.item(1).json;

try {
  const ticker = webhookInput.ticker?.toUpperCase();
  const notes = webhookInput.notes || ;

  if (!ticker) {
    throw new Error(Ticker is required);
  }

  // Check if ticker already exists
  const existingStock = screenerData.stocks.find(s => s.ticker === ticker);

  if (existingStock) {
    return [{
      json: {
        status: error,
        message: `Stock ${ticker} already exists in screener`,
        existing_stock: existingStock
      }
    }];
  }

  // Create new stock entry
  const newStock = {
    ticker: ticker,
    signal: yellow,
    research_status: pending,
    notes: notes,
    added_date: new Date().toISOString(),
    last_updated: new Date().toISOString()
  };

  // Add to stocks array
  screenerData.stocks.push(newStock);

  // Update metadata
  screenerData.metadata = screenerData.metadata || {};
  screenerData.metadata.total_stocks = screenerData.stocks.length;
  screenerData.metadata.last_updated = new Date().toISOString();

  // Count signals
  const signals = screenerData.stocks.reduce((acc, s) => {
    acc[s.signal] = (acc[s.signal] || 0) + 1;
    return acc;
  }, {});
  screenerData.metadata.signal_counts = signals;

  return [{
    json: {
      updatedScreener: screenerData,
      addedStock: newStock,
      status: success
    }
  }];

} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Whizkid Add Stock V2,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Adding stock to screener,
        original_input: webhookInput,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Whizkid Add Stock V2,
      timestamp: new Date().toISOString()
    }
  }];
}
