const input = $(Webhook).first().json.body;
const screenerData = $input.first().json;

try {
  const ticker = input.ticker;

  if (!ticker) {
    return [{
      json: {
        status: error,
        error: Missing ticker parameter,
        agent_name: Whizkid Remove Stock,
        timestamp: new Date().toISOString()
      }
    }];
  }

  const stocks = screenerData.stocks || [];
  const stockIndex = stocks.findIndex(s => s.ticker === ticker);

  if (stockIndex === -1) {
    return [{
      json: {
        status: error,
        error: `Stock with ticker ${ticker} not found in screener`,
        agent_name: Whizkid Remove Stock,
        timestamp: new Date().toISOString()
      }
    }];
  }

  const removedStock = stocks[stockIndex];
  const signal = removedStock.signal;

  stocks.splice(stockIndex, 1);

  const metadata = screenerData.metadata || {};
  const signalDistribution = metadata.signal_distribution || {};

  if (metadata.total_positions) {
    metadata.total_positions--;
  }

  if (signalDistribution[signal]) {
    signalDistribution[signal]--;
  }

  metadata.last_updated = new Date().toISOString();

  const updatedScreener = {
    ...screenerData,
    stocks: stocks,
    metadata: {
      ...metadata,
      signal_distribution: signalDistribution
    }
  };

  return [{
    json: {
      updatedScreener: updatedScreener,
      removedTicker: ticker,
      removedSignal: signal
    }
  }];
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Whizkid Remove Stock,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Removing stock from screener,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Whizkid Remove Stock,
      timestamp: new Date().toISOString()
    }
  }];
}

const previousData = $(Remove Stock).first().json;
const updatedScreener = previousData.updatedScreener;

try {
  const remainingCount = updatedScreener.metadata.total_positions || 0;
  const removedTicker = previousData.removedTicker;
  const removedSignal = previousData.removedSignal;

  return [{
    json: {
      status: success,
      message: `Successfully removed ${removedTicker} from screener`,
      removed_ticker: removedTicker,
      removed_signal: removedSignal,
      remaining_positions: remainingCount,
      timestamp: new Date().toISOString()
    }
  }];
} catch (error) {
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Whizkid Remove Stock,
      timestamp: new Date().toISOString()
    }
  }];
}
