const fs = require(fs);
const path = require(path);
const input = $input.first().json.body || $input.first().json;

try {
  const filePath = /data/investment-data/whizkid-screener.json;

  if (!fs.existsSync(filePath)) {
    throw new Error(NEED_TOOL: File system access to read /data/investment-data/whizkid-screener.json);
  }

  const fileContent = fs.readFileSync(filePath, utf8);
  const data = JSON.parse(fileContent);

  const signalEmoji = {
    green: 游릭,
    yellow: 游리,
    red: 游댮
  };

  let markdown = `# Whizkid Stock Screener\n\n`;
  markdown += `**Last Updated:** ${data.last_updated}\n\n`;
  markdown += `## Positions\n\n`;
  markdown += `| Signal | Ticker | Company | Sector | Price | Market Cap (B) | Revenue Growth | Gross Margin | P/S |\n`;
  markdown += `|--------|--------|---------|--------|-------|----------------|----------------|--------------|-----|\n`;

  data.stocks.forEach(stock => {
    const emoji = signalEmoji[stock.signal.toLowerCase()] || stock.signal;
    const price = typeof stock.current_price === number ? `$${stock.current_price.toFixed(2)}` : stock.current_price;
    const marketCap = typeof stock.market_cap_b === number ? `$${stock.market_cap_b.toFixed(2)}B` : stock.market_cap_b;
    const revenueGrowth = typeof stock.revenue_growth_yoy === number ? `${stock.revenue_growth_yoy.toFixed(1)}%` : stock.revenue_growth_yoy;
    const grossMargin = typeof stock.gross_margin === number ? `${stock.gross_margin.toFixed(1)}%` : stock.gross_margin;
    const ps = typeof stock.price_to_sales === number ? stock.price_to_sales.toFixed(2) : stock.price_to_sales;

    markdown += `| ${emoji} | ${stock.ticker} | ${stock.company} | ${stock.sector} | ${price} | ${marketCap} | ${revenueGrowth} | ${grossMargin} | ${ps} |\n`;
  });

  markdown += `\n## Summary\n\n`;
  markdown += `**Total Positions:** ${data.metadata.total_positions}\n\n`;
  markdown += `**Signal Distribution:**\n`;
  markdown += `- 游릭 Green: ${data.metadata.signal_distribution.green}\n`;
  markdown += `- 游리 Yellow: ${data.metadata.signal_distribution.yellow}\n`;
  markdown += `- 游댮 Red: ${data.metadata.signal_distribution.red}\n`;

  return [{
    json: {
      status: success,
      markdown: markdown,
      data: data,
      timestamp: new Date().toISOString()
    }
  }];
} catch (error) {
  if (error.message.includes(NEED_TOOL:)) {
    return [{
      json: {
        status: tool_request,
        agent_name: Whizkid View Screener V2,
        tool_needed: error.message.replace(NEED_TOOL:, ).trim(),
        context: Reading and formatting stock screener data,
        original_input: input,
        timestamp: new Date().toISOString()
      }
    }];
  }
  return [{
    json: {
      status: error,
      error: error.message,
      agent_name: Whizkid View Screener V2,
      timestamp: new Date().toISOString()
    }
  }];
}
